mapper.update:
UPDATE attendance
ayear = #{ayear}, amonth = #{amonth}, alatecome = #{alatecome}, aearlyleave=#{aearlyleave}, workovertime=#{workovertime}, aleave=#{aleave}, wovertime=#{wovertime}, hovertime=#{hovertime},
attendance.aid = #{aid}

mapper.select:
SELECT attendance.aid, attendance.uempid, attendance.uempname, attendance.ayear, attendance.amonth, attendance.alatecome, attendance.aearlyleave, attendance.aearlycome, attendance.workovertime, attendance.aleave, attendance.bonusres, attendance.wovertime, attendance.hovertime FROM attendance WHERE 1 = 1
AND attendance.uempid like CONCAT('%',#{uempid},'%')
AND attendance.uempname like CONCAT('%',#{uempname},'%')
AND attendance.ayear = #{ayear}
AND attendance.amonth = #{amonth}
